package calci;

public class calciUtil {

	
	
	
	public calciUtil() {
		super();
		// TODO Auto-generated constructor stub
	}

	int result = 0;
	
	public int add(int a , int b){
		
		result = a+b;
		
		return result;
	}
	
	
	public int subtract(int a, int b) {

		result = a - b;

		return result;
	}
	
	
	
	public int multiply(int a, int b) {

		result = a * b;

		return result;
	}
	
	public int devide(int a, int b) {

		result = a / b;

		return result;
	}
	
}
